import React, { useState } from 'react';
import ProductList from './components/ProductList';

import { Product } from './types';

const initialProducts: Product[] = [
    {
        id: 1,
        imageUrl: 'https://images.prom.ua/1289180610_w$%7Bwidth%7D_h$%7Bheight%7D_vse-pro-mango.jpg',
        name: 'Mango',
        count: 5,
        size: { width: 100, height: 100 },
        weight: '200g',
        comments: [
            {
                id: 1,
                productId: 1,
                description: 'Fresh and juicy',
                date: '14:00 22.08.2021',
            },
        ],
    },
    {
        id: 2,
        imageUrl: 'https://www.quanta.org/thumbs/thumb-orange-640x480-orange.jpg',
        name: 'Orange',
        count: 3,
        size: { width: 120, height: 80 },
        weight: '300g',
        comments: [],
    },
];
const App: React.FC = () => {
    const [products, setProducts] = useState<Product[]>(initialProducts);
    const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('asc');

    const handleDeleteProduct = (id: number) => {
        const confirmed = window.confirm('Ви впевнені, що хочете видалити продукт?');
        if (confirmed) {
            setProducts(products.filter(p => p.id !== id));
        }
    };

    const toggleSortOrder = () => {
        const newOrder = sortOrder === 'asc' ? 'desc' : 'asc';
        setSortOrder(newOrder);

        const sortedProducts = [...products].sort((a, b) => {
            return newOrder === 'asc'
                ? a.name.localeCompare(b.name)
                : b.name.localeCompare(a.name);
        });

        setProducts(sortedProducts);
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Products List View</h2>
            <button onClick={toggleSortOrder}>
                Сортувати {sortOrder === 'asc' ? 'А→Я' : 'Я→А'}
            </button>
            <ProductList products={products} onDelete={handleDeleteProduct} />
        </div>
    );
};
