import React, { useState } from 'react';

interface Comment {
    id: number;
    productId: number;
    description: string;
    date: string;
}

interface Product {
    id: number;
    imageUrl: string;
    name: string;
    count: number;
    size: {
        width: number;
        height: number;
    };
    weight: string;
    comments: Comment[];
}

const initialProducts: Product[] = [
    {
        id: 1,
        imageUrl: 'https://via.placeholder.com/150',
        name: 'Apple',
        count: 5,
        size: { width: 100, height: 100 },
        weight: '200g',
        comments: [
            {
                id: 1,
                productId: 1,
                description: 'Fresh and juicy',
                date: '14:00 22.08.2021',
            },
        ],
    },
    {
        id: 2,
        imageUrl: 'https://via.placeholder.com/150',
        name: 'Banana',
        count: 3,
        size: { width: 120, height: 80 },
        weight: '300g',
        comments: [],
    },
];

const ProductList: React.FC = () => {
    const [products, setProducts] = useState<Product[]>(initialProducts);

    const handleAddProduct = () => {
        alert('Показати модальне вікно для додавання продукту (поки що не реалізовано)');
    };

    const handleDeleteProduct = (id: number) => {
        const confirmed = window.confirm('Ви впевнені, що хочете видалити продукт?');
        if (confirmed) {
            setProducts(products.filter(p => p.id !== id));
        }
    };

    return (
        <div style={{ padding: '20px' }}>
            <h2>Products List View</h2>
            <button onClick={handleAddProduct}>Add product</button>
            <ul>
                {products.map(p => (
                    <li key={p.id} style={{ marginBottom: '20px', listStyle: 'none' }}>
                        <img src={p.imageUrl} alt={p.name} width={100} />
                        <h3>{p.name}</h3>
                        <p>Count: {p.count}</p>
                        <p>Size: {p.size.width}x{p.size.height}</p>
                        <p>Weight: {p.weight}</p>
                        <button onClick={() => handleDeleteProduct(p.id)}>Delete</button>

                        <div style={{ marginTop: '10px' }}>
                            <strong>Comments:</strong>
                            <ul>
                                {p.comments.length > 0 ? (
                                    p.comments.map(c => (
                                        <li key={c.id}>
                                            {c.description} – <em>{c.date}</em>
                                        </li>
                                    ))
                                ) : (
                                    <li>No comments</li>
                                )}
                            </ul>
                        </div>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProductList;
