import React, { useState } from 'react';

interface Product {
    id: number;
    name: string;
    count: number;
}

const initialProducts: Product[] = [
    { id: 1, name: 'Apple', count: 5 },
    { id: 2, name: 'Banana', count: 3 },
    { id: 3, name: 'Orange', count: 8 },
];

const ProductList: React.FC = () => {
    const [products, setProducts] = useState(initialProducts);

    const handleAddProduct = () => {
        alert('Показати модальне вікно для додавання продукту');
    };

    const handleDeleteProduct = (id: number) => {
        const confirmed = window.confirm('Ви впевнені, що хочете видалити продукт?');
        if (confirmed) {
            setProducts(products.filter(p => p.id !== id));
        }
    };

    return (
        <div>
            <h2>Products List View</h2>
            <button onClick={handleAddProduct}>Add product</button>
            <ul>
                {products.map(p => (
                    <li key={p.id}>
                        {p.name} (Count: {p.count})
                        <button onClick={() => handleDeleteProduct(p.id)} style={{marginLeft: '10px'}}>
                            Delete
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ProductList;
